"use strict";

//------------------------------------------------------------------------------
// Rule Definition
//------------------------------------------------------------------------------

module.exports = {
    meta: {
        docs: {
            description: "check if html gets generated in the wrong way",
            category: "testat",
            recommended: true
        },
        fixable: null,
        schema: []
    },

    create: function (context) {
        return {
            "MemberExpression[object.name='document']": (node) => {
                if (node) {
                    if (node.property.name === "createElement") {
                        context.report({
                            node,
                            message: "Use handlebars or template strings for html generation.",
                            type: "problem"
                        });
                    }
                }
            }
        };
    }
};
