"use strict";

module.exports = {
    meta: {
        docs: {
            description: "no while are allowed",
            category: "testat",
            recommended: true
        },
        fixable: null,  // or "code" or "whites pace"
        schema: [

        ]
    },

    create: function(context) {
        return {
            WhileStatement(node) {
                if (node) {
                    context.report({
                        node,
                        message: "No while loop allowed.",
                        type: "problem"
                    });
                }
            }
        };
    }
};
