"use strict";

//------------------------------------------------------------------------------
// Requirements
//------------------------------------------------------------------------------

const rule = require("../../../lib/rules/use-action-map");
const RuleTester = require("eslint").RuleTester;


//------------------------------------------------------------------------------
// Tests
//------------------------------------------------------------------------------

const ruleTester = new RuleTester();
ruleTester.run("no-else-if", rule, {
    valid: [
        {
            code: "if(x){}",
        },
        {
            code: "if(x){}console.log();if(y){}",
        }
    ],
    invalid: [
        {
            code: "switch (x) {case '': break;}",
            errors: [{
                message: "Use action map instead if switch case.",
                type: "SwitchStatement"
            }]
        },
        {
            code: "if(x){}else if(y){ }else if(y){ }",
            errors: [{
                message: "Use action map instead if - else if - else if.",
                type: "IfStatement"
            }]
        },
        {
            code: "if(a){}if(b){}",
            errors: [{
                message: "Use action map instead if - if - ...",
                type: "IfStatement"
            }]
        }
    ]
});
